jQuery(document).ready(function(a) {
    if(jQuery("#rev_auto_import").val()=='Disabled')
    {
      jQuery(".rev_import_section").hide();
    }
    else
    {
      jQuery(".rev_import_section").show();
    }
    if(jQuery("#rev_auto_export").val()=='Disabled')
    {
      jQuery(".rev_export_section").hide();
    }
    else
    {
      jQuery(".rev_export_section").show();
    }
    jQuery( "#datepicker1" ).datepicker({
        dateFormat: "yy-mm-dd",
      changeMonth: true,//this option for allowing user to select month
      changeYear: true //this option for allowing user to select from year range
    });
    jQuery( "#datepicker2" ).datepicker({
        dateFormat: "yy-mm-dd",
      changeMonth: true,//this option for allowing user to select month
      changeYear: true //this option for allowing user to select from year range
    });
    "use strict";
    a("#rev_enable_ftp_ie").click(function () {
        if (this.checked) {
            a("#rev_export_section_all").show();
        }else{
            a("#rev_export_section_all").hide();
        }
    });
    a("select[name=rev_auto_export]").change(function() {
        if("Disabled" === a(this).val()){
            a(".rev_export_section").hide();
        }else{
            a(".rev_export_section").show();
        }
    })
     if(woocommerce_review_csv_cron_params.rev_enable_ftp_ie !=1){
        a("#rev_export_section_all").hide();
    };
    // if(woocommerce_review_csv_cron_params.auto_export === 'Disabled'){
    //     a(".export_section").hide();
    // };
    a("select[name=rev_auto_export]").change(function() {
        if("Disabled" === a(this).val()){
            a(".rev_export_section").hide();
        }else{
            a(".rev_export_section").show();
        }
    })

    a("select[name=rev_auto_import]").change(function() {
        if("Disabled" === a(this).val()){
            a(".rev_import_section").hide();
        }else{
            a(".rev_import_section").show();
        }
    })
    // if(woocommerce_review_csv_cron_params.auto_import === 'Disabled'){
    //     a(".import_section").hide();
    // }
});